mapper.insert:
INSERT INTO BOARD
VALUES (NVL((SELECT MAX(IDX) + 1
               FROM BOARD), 1)
       ,#{ID}
       ,#{TITLE}
       ,#{CONTENT}
       ,SYSDATE
       ,0
       ,#{LOCK}
       ,#{PROGRAM}
)

INSERT INTO REPLY
VALUES (NVL((SELECT MAX(IDX) + 1
               FROM REPLY), 1)
       ,#{B_IDX}
       ,#{ID}
       ,#{CONTENT}
       ,SYSDATE
)
