mapper.insert:

INSERT INTO VOD
    VALUES(NVL((SELECT MAX(IDX) + 1
                  FROM VOD
                  WHERE #{CODE} < IDX
                    AND IDX < (#{CODE} + 10000)), #{CODE} + 1)
          ,#{TITLE}
          ,#{CATEGORY}
          ,#{CONTENT}
          ,0
          ,SYSDATE)

mapper.select:
SELECT * FROM VOD WHERE 1=1
AND TITLE = #{TITLE}
AND IDX = #{IDX}
AND TRUNC(SYSDATE, 'MM') <= U_DATE
ORDER BY VIEW_COUNT DESC

mapper.update:
UPDATE VOD
   SET CONTENT = #{CONTENT}
 WHERE IDX = #{IDX}

UPDATE VOD
   SET VIEW_COUNT = VIEW_COUNT + 1
 WHERE IDX = #{IDX}
